  {{- $disabled := .Params.disable_sitemap -}}
  {{- if not $disabled -}}
    {{- $pageLen := add $pageLen 1 }}
    {{- $paths := split .File.Dir "/" }}
    {{- $main := index $paths 0 }}
    {{- $space := index $spaces $main -}}
    {{- $newUrl := replace .Permalink "index.html" "" -}}
    {{- $newUrl := replace $newUrl ".html" "" }}

  {{ $newUrl }}{{ if not .Lastmod.IsZero }}
    {{- /* -}}{{ safeHTML ( .Lastmod.Format "2006-01-02T15:04:05-07:00" ) }}{{- */ -}}{{ end }}
    {{ with .Params.priority }}
  {{ add "0." (string . ) }}
    {{- else -}}
      {{- with $space.priority -}}
  {{ add "0." (string . ) }}
      {{- else -}}
  0.5
      {{- end -}}
    {{- end -}}

  {{- end -}}
